> README.md &&
for x in overview webhook client; do cat "docs/"${x}".md" >> README.md; echo '\n\n' >> README.md; done &&
echo "## Reference" >> README.md && 
markdown-toc --maxdepth=2 --no-firsth1 -i README.md
documentation build lib/** -f md >> README.md &&
sed -i "" "/\#\#\# Table of Contents/d" README.md